user 'test-user' do
  manage_home true
  supports :manage_home => true
  home '/home/test-user'
  action [:create, :manage]
end

ssh_authorized_keys 'first' do
  user 'test-user'
  type 'id-rsa'
  options 'no-agent-forwarding' => nil, 'no-pty' => nil, :command => 'ls /root'
  comment 'I left a comment'
  key '' # rubocop:disable all
end

ssh_authorized_keys 'without comment' do
  user 'test-user'
  type 'id-rsa'
  key '' # rubocop:disable all
end

ssh_authorized_keys 'type defaults' do
  user 'test-user'
  key '' # rubocop:disable all
end
